// Callback event types for pl_cam_register_callback_ex3 (bd-ek9n.2)
#[cfg(feature = "pvcam-sdk")]
pub const PL_CALLBACK_BOF: i32 = 0; // Beginning of frame
#[cfg(feature = "pvcam-sdk")]
pub const PL_CALLBACK_EOF: i32 = 1; // End of frame (frame ready)

// NOTE: FRAME_INFO structure is generated by bindgen from the SDK headers.
// We use the bindgen-generated FRAME_INFO (type alias to _TAG_FRAME_INFO).
// Fields include: FrameInfoGUID, hCam, FrameNr, TimeStamp, ReadoutTime, TimeStampBOF
// FrameNr is the hardware frame number (1-based) used for frame loss detection (bd-ek9n.3).

/// Callback function type for pl_cam_register_callback_ex3 (bd-ek9n.2)
///
/// The callback is invoked by PVCAM on EOF events. Keep work minimal:
/// - Update frame-ready signal
/// - Do NOT block or perform lengthy operations
/// - Frame retrieval happens outside the callback
///
/// Uses `extern "system"` for ABI compatibility:
/// - On Windows: `__stdcall` (matches PVCAM's PV_DECL)
/// - On Unix: equivalent to `extern "C"`
#[cfg(feature = "pvcam-sdk")]
pub type PvcamCallback = unsafe extern "system" fn(pFrameInfo: *const FRAME_INFO, pContext: *mut std::ffi::c_void);

// Manual declaration for pl_cam_register_callback_ex3 (bd-ek9n.2)
// This function may not be generated by bindgen if the header version differs.
// Signature: rs_bool pl_cam_register_callback_ex3(int16 hcam, int32 callback_event, void* callback, void* context)
// Uses extern "system" to match PVCAM's PV_DECL (__stdcall on Windows, cdecl on Unix)
//
// NOTE: Using renamed symbols with #[link_name] to avoid conflicts with bindgen-generated
// symbols on SDK versions where bindgen does export these functions.
#[cfg(feature = "pvcam-sdk")]
extern "system" {
    /// Register a callback for camera events (EOF, BOF, etc.)
    ///
    /// # Arguments
    /// - `hcam`: Camera handle
    /// - `callback_event`: Event type (PL_CALLBACK_EOF or PL_CALLBACK_BOF)
    /// - `callback`: Function pointer to the callback
    /// - `context`: User context passed to callback
    ///
    /// # Returns
    /// Non-zero on success, zero on failure
    #[link_name = "pl_cam_register_callback_ex3"]
    pub fn pl_cam_register_callback_ex3_manual(
        hcam: i16,
        callback_event: i32,
        callback: PvcamCallback,
        context: *mut std::ffi::c_void,
    ) -> rs_bool;

    /// Unregister a previously registered callback
    ///
    /// # Arguments
    /// - `hcam`: Camera handle
    /// - `callback_event`: Event type to unregister
    ///
    /// # Returns
    /// Non-zero on success, zero on failure
    #[link_name = "pl_cam_deregister_callback"]
    pub fn pl_cam_deregister_callback_manual(
        hcam: i16,
        callback_event: i32,
    ) -> rs_bool;
}

#[cfg(not(feature = "pvcam-sdk"))]
/// Placeholder module when the `pvcam-sdk` feature is not enabled.
/// No actual PVCAM functions or types are available in this configuration.
pub mod pvcam_bindings {
    // Define minimal types or functions if needed for API compatibility
    // but for a -sys crate, an empty module is often sufficient.
}
